def find_basement(floor_input, a, b):
  running_count = 0
  index_count = 0
  split_string = list(floor_input)
  print(split_string)
  for x in split_string:
    if x == a:
      running_count += 1
      index_count += 1
    if x == b:
      running_count -= 1
      index_count += 1
      if running_count < 0:
        return index_count
      


